import styled from "styled-components"
import { ReactFragment } from "react"

const Container = styled.div`
    width: 100vw;
    height: 100vh;
    background: url("https://wallpaperaccess.com/full/542637.jpg") center;
    display: flex;
    align-items: center;
    justify-content: center;
    background-size: cover;
`

const Wrapper = styled.div`
    width: 25%;
    padding: 20px;
    background-color: white;
    `
const Form = styled.form`
    display: flex;
    flex-direction: column;
    `
const Title = styled.h1`
    font-size: 24px;
    font-weight: 300;
    `
const Input = styled.input`
    flex: 1;
    min-width: 40%;
    margin: 10px 0px;
    padding: 10px;
    `
const Button = styled.button`
    width: 40%;
    border: none;
    padding: 15px 20px;
    background-color: teal;
    color: white;
    cursor: pointer;
    margin-bottom: 10px;
`

const Link = styled.a`
    margin: 10px 0px;
    font-size: 12px;
    text-decoration: underline;
    cursor: pointer;
`

const Login = () => {
    return (
        <ReactFragment>
            <Container>
                <Wrapper>
                    <Title>
                        SIGN IN
                    </Title>
                    <Form>
                        <Input placeholder="name"/>
                        <Input placeholder="password"/>
                    </Form>
                    <Button>
                        LOGIN
                    </Button>
                    <Link>Create new Account</Link>
                </Wrapper>
            </Container>
        </ReactFragment>
      )
}

export default Login
